import React from 'react';
import RepositoryItem from '../repository-item';
import * as S from './styled';
import useGithub from '../hooks/github-hooks';

const Repositories = () => {
    const { githubState, getUserRepos, getUserStarred } = useGithub();
    const [hasUserForSearchrepos, setHasUserForSearchrepos] = useState(false);
    return (
        <S.WrapperTabs
        selectedTabClassName='is-selected'
        selectedTabPanelClassName='is-selected'
        >
            <S.WrapperTabList>
                <S.WrapperTab>Repositories</S.WrapperTab>
                <S.WrapperTab>Starred</S.WrapperTab>
            </S.WrapperTabList>
                <S.WrapperTabPanel>
                    <RepositoryItem
                        name="dio-desafio-github-primeiro-repositorio"
                        linkToRepo="https://github.com/GeoMorttis/dio-desafio-github-primeiro-repositorio"
                        fullName="GeoMorttis/dio-desafio-github-primeiro-repositorio"     
                    />
                </S.WrapperTabPanel>
                <S.WrapperTabPanel>
                    <RepositoryItem
                        name="dio-desafio-html-css-segundo-repositorio"
                        linkToRepo="https://github.com/GeoMorttis/dio-desafio-html-css-segundo-repositorio"
                        fullName="GeoMorttis/dio-desafio-html-css-segundo-repositorio"     
                    />
                </S.WrapperTabPanel>
        </S.WrapperTabs>
    );
};

export default Repositories;
