import styled from "styled-components";

export const Wrapper = styled.div`
    display: flex;
    width: 100%;
    justify-content: space-between;
    padding: 4px;

    input {
        border: 1px solid #ccc;
        border-radius: 8px;
        width: 100%;
        height: 35px;       
    }

    button {
        background-color: #ccc;
        padding: 8px 16px;
        margin: 0 8px;
    }

`;
