import React, { createContext, useState } from 'react';
import api from './services/api';

export const GithubContext = createContext ( {
    loading: false,
    user: {},
    repositories: [],
    starred: [],
});

const GithubProvider = ({ children }) => {
    const [githubState, setGithubState] = useState ({
        loading: false,
        user: {
            login: undefined,
            name: undefined,
            html_url: undefined,
            blog: undefined,
            company: undefined,
            location: undefined,
            followers: 0,
            following: 0,
            public_gists: 0,
            public_repos: 0,
        },
        repositories: [],
        starred: [],
    });

    const getUser = (username) => {
        api.get(`users/${username}`).then(({ data: { user } }) => {
            setGithubState ((prevState) => ({ 
                ...prevState,
                user: {
                    login: user.data,
                    name: user.data,
                    html_url: user.data,
                    blog: user.data,
                    company: user.data,
                    location: user.data,
                    followers: user.data,
                    following: user.data,
                    public_gists: user.data,
                    public_repos: user.data,
                },
            }));
        });
    };

    const contextValue = {
        githubState,
    };

    return (
        <GithubContext.Provider value={contextValue}>
            {children}
        </GithubContext.Provider>
    );
};

export default GithubProvider;
